import Owner from "./components/Owner";
import Voter from "./components/Voter";
import "./style/style.css";
import { EthProvider, useEth } from "./contexts/EthContext";
import { useEffect, useState, useCallback } from "react";

function App() {
  const { state } = useEth();
  const { contract, accounts } = state;

  const [isOwner, setIsOwner] = useState(false);
  const [isVoter, setIsVoter] = useState(false);
  const [proposals, setProposals] = useState([]);

  const getProposals = useCallback(async () => {
    let proposalEvents = await contract.getPastEvents("ProposalRegistered", {
      fromBlock: 0,
      toBlock: "latest",
    });
    console.log(proposalEvents);
    let proposalsArray = [];

    for (let i = 0; i < proposalEvents.length; i++) {
      let proposalID = proposalEvents[i].returnValues[0];

      const res = await contract.methods
        .getOneProposal(proposalID)
        .call({ from: accounts[0] });

      proposalsArray.push(res);
    }
    console.log(proposalsArray);
    setProposals(proposalsArray);
  }, [contract, accounts]);

  const setProposalRegisteredEvent = useCallback(async () => {
    await contract.events
      .ProposalRegistered()
      .on("data", (event) => {
        getProposals();
      })
      .on("changed", (changed) => console.log(changed))
      .on("error", (err) => console.log(err))
      .on("connected", (str) => console.log(str));
  }, [getProposals]);
  const setCountRefresh = useCallback(async () => {
    await contract.events
      .ProposalRegistered()
      .on("data", (event) => {
        getProposals();
      })
      .on("changed", (changed) => console.log(changed))
      .on("error", (err) => console.log(err))
      .on("connected", (str) => console.log(str));
  }, [getProposals]);

  const getOwner = useCallback(async () => {
    //***S'inspirer de getOwner pour faire le getProposals
    // faire useEffect
    // Get past events
    // boucle for
    // setPropals(proposals)
    const ownerAddress = await contract.methods.owner().call();
    if (ownerAddress === accounts[0]) {
      setIsOwner(true);
    }
  }, [contract, accounts, setIsOwner]);

  // const getVoter = useCallback(async () => {
  //   const voter = await contract.methods.getVoter(accounts[0]).call();
  // }, [contract]);

  useEffect(() => {
    // se relance à chaque fois que le contrat change de valeur

    if (contract) {
      setProposalRegisteredEvent();
      setCountRefresh();
      getOwner();
      getProposals();
      // getVoter();
    }
  }, [
    getOwner,
    contract,
    setProposalRegisteredEvent,
    setCountRefresh,
    getProposals,
  ]);
  // If Ok
  // setIsOwner(true)

  return (
    <div className="w-screen h-screen overflow-auto">
      {isOwner && <Owner />}
      <Voter proposals={proposals} />
    </div>
  );
}

export default App;
