import useEth from "../contexts/EthContext/useEth";

function Owner() {
  const {
    state
  } = useEth();

  const  { contract, accounts } = state

  // const { state } = useContext(EthContext);

  //const res = await contract.methods.startProposalsRegistering().send({
  //  from: accounts[0]

  const addVoter = () => {
    alert("Voter added!");
  };

  const startProposalsRegistering = async () => {
    console.log(contract.methods);
    const result = await contract.methods.startProposalsRegistering().send({
      from: accounts[0]
      //trigger event ProposalsRegistrationStarted
    });
    console.log(result);
  };

  const endProposalsRegistering = () => {
    alert("Proposal registering closed");
  };
  const startVotingSession = () => {
    alert("Voting session open now!");
  };
  const endVotingSession = () => {
    alert("Voting session closed!");
  };

  const tallyVotes = () => {
    alert("Votes tallied!");
  };

  return (
    <div className="wrapDiv">
      <div className="userAdd">
        <h1>Votre addresse de wallet: 0xkjhkijgdqsd14545454qsdqsd</h1>
      </div>
      <div className="addVoter">
        <br />
        <h1 className="text-2xl">Ajout voter</h1>
        <br />
        <h2>Adresse user: </h2>
        <br />
        <input className="input" type="text" />
        <br />
        <br />
        <button display="block" className="btn" onClick={addVoter}>
          Add voter
        </button>
        <br />
      </div>
      <br />
      <div className="startRegister">
        <h1 className="text-2xl">Start registering</h1>
        <br />
        <button className="btn" onClick={startProposalsRegistering}>
          Open registering
        </button>
      </div>
      <br />
      <div className="endRegister">
        <h1 className="text-2xl">End registering</h1>
        <br />

        <button className="btn" onClick={endProposalsRegistering}>
          Close registering
        </button>
      </div>
      <div className="startVoting">
        <h1 className="text-2xl">startVoting</h1>
        <br />
        <button className="btn" onClick={startVotingSession}>
          Start Voting session
        </button>
      </div>
      <div className="endVoting">
        <h1 className="text-2xl">End voting session</h1>
        <br />
        <button className="btn" onClick={endVotingSession}>
          Close registering
        </button>
      </div>
      <div className="tallyVotes">
        <h1 className="text-2xl">Tally votes</h1>
        <br />
        <button className="btn" onClick={tallyVotes}>
          Tally votes
        </button>
      </div>
    </div>
  );
}

export default Owner;
