import useEth from "../contexts/EthContext/useEth";
import { useState } from "react";

function Owner() {
  const { state } = useEth();
  const [address, setAddress] = useState(null);

  const { contract, accounts } = state;

  function handleChange(event) {
    console.log(event.target.value);
  }

  const addVoter = async () => {
    const result = await contract.methods.addVoter({ address }).send({
      from: accounts[0],
    });
    console.log(result);
  };
  const startProposalsRegistering = async () => {
    const result = await contract.methods.startProposalsRegistering().send({
      from: accounts[0],
    });
    console.log(result);
  };
  const endProposalsRegistering = async () => {
    const result = await contract.methods.endProposalsRegistering().send({
      from: accounts[0],
    });
    console.log(result);
  };
  const startVotingSession = async () => {
    const result = await contract.methods.startVotingSession().send({
      from: accounts[0],
    });
    console.log(result);
  };
  const endVotingSession = async () => {
    const result = await contract.methods.endVotingSession().send({
      from: accounts[0],
    });
    console.log(result);
  };
  const tallyVotes = async () => {
    const result = await contract.methods.tallyVotes().send({
      from: accounts[0],
    });
    console.log(result);
  };

  return (
    <div className="wrapDiv">
      <div className="userAdd">
        <h1>Votre addresse de wallet: {state.accounts}</h1>
      </div>
      <div className="addVoter">
        <br />
        <h1 className="text-2xl">Ajout voter</h1>
        <br />
        <h2>Adresse user: </h2>
        <br />
        <input type={"text"} name="addVoter" onChange={handleChange} />
        <br />
        <button display="block" className="btn" onClick={addVoter}>
          Add voter
        </button>
        <br />
      </div>
      <br />
      <div className="startRegister">
        <h1 className="text-2xl">Start registering</h1>
        <br />
        <button className="btn" onClick={startProposalsRegistering}>
          Open registering
        </button>
      </div>
      <br />
      <div className="endRegister">
        <h1 className="text-2xl">End registering</h1>
        <br />

        <button className="btn" onClick={endProposalsRegistering}>
          Close registering
        </button>
      </div>
      <div className="startVoting">
        <h1 className="text-2xl">startVoting</h1>
        <br />
        <button className="btn" onClick={startVotingSession}>
          Start Voting session
        </button>
      </div>
      <div className="endVoting">
        <h1 className="text-2xl">End voting session</h1>
        <br />
        <button className="btn" onClick={endVotingSession}>
          Close registering
        </button>
      </div>
      <div className="tallyVotes">
        <h1 className="text-2xl">Tally votes</h1>
        <br />
        <button className="btn" onClick={tallyVotes}>
          Tally votes
        </button>
      </div>
    </div>
  );
}

export default Owner;
