import useEth from "../contexts/EthContext/useEth";
import { useState, useEffect, useCallback } from "react";

function Voter({ web3 }) {
  console.log(web3);
  const { state } = useEth();
  const { contract, accounts } = state;
  const [isVoter, setIsVoter] = useState(false);
  const [proposal, setProposal] = useState(null);
  const [proposals, setProposals] = useState([]);

  const checkIfVoter = useCallback(async () => {
    //***S'inspirer de getOwner pour faire le getProposals
    // faire useEffect
    // Get past events
    // boucle for
    // setPropals(proposals)
    const voterAddress = await contract.methods.voter().call();
    if (voterAddress === accounts[0]) {
      setIsVoter(true);
    }
  }, [contract, accounts, setIsVoter]);

  const getProposals = useCallback(async () => {
    let proposalEvents = await contract.getPastEvents("ProposalRegistered");

    let proposalArray = [];

    for (let i = 0; i < proposalEvents.length; i++) {
      let proposalID = proposalEvents[i].returnValues[0];
      const res = await contract.methods
        .getOneProposal(proposalID)
        .call({ from: accounts[0] });

      proposalArray.push(res);
    }
    console.log(proposalArray);
    setProposals(proposalArray);
  }, [contract, accounts, setIsVoter]);

  useEffect(() => {
    // se relance Ã  chaque fois que le contrat change de valeur
    if (contract) {
      // checkIfVoter();
      getProposals();
    }
  }, [getProposals, contract, checkIfVoter]);

  function handleChange(event) {
    setProposal(event.target.value);
  }

  const setVote = async () => {
    const result = await contract.methods.setVote().send({
      from: accounts[0],
    });
    console.log(result);
  };
  const addProposal = async () => {
    const result = await contract.methods.addProposal(proposal).send({
      from: accounts[0],
    });
    console.log(result);
  };
  const getVoter = async () => {
    const result = await contract.methods.getVoter().call();
    console.log(result);
  };

  const getOneProposal = async () => {
    const result = await contract.methods.getOneProposal().call();
    console.log(result);
  };

  return (
    <>
      <div className="addProposal">
        <br />
        <h1 className="text-2xl">Add a proposal</h1>
        <input
          className="input"
          type={"text"}
          name="addProposal"
          onChange={handleChange}
        />

        <button display="block" className="btn" onClick={addProposal}>
          Click to add your proposal
        </button>
        <br />
      </div>
      <div className="getProp">
        <br />
        <h1 className="text-2xl">See a proposal</h1>
        <button className="btn" onClick={getOneProposal}>
          Get proposal
        </button>
        <br />
      </div>
      <div className="getVoter">
        <br />
        <h1 className="text-2xl">Voter</h1>
        <input className="input" type="text" id="voter" />
        <button className="btn" onClick={getVoter}>
          Get voter
        </button>
        <br />
      </div>
      <div className="setVote">
        <br />
        <h1 className="text-2xl">Choose a proposal</h1>
        <br />
        <ul>
          {proposals.map((proposal) => (
            <li>
              <p>{proposal.description}</p>
              <button onClick={setVote}>Yes!</button>
            </li>
          ))}
        </ul>
        <br />
        <br />
      </div>
    </>
  );
}

export default Voter;
