export default Voter;

function Voter({ web3 }) {
  console.log(web3);
  //  const res = await contract.methods.startProposalsRegistering().send({
  //    from: accounts[0]
  //})

  const setVote = () => {
    alert("Has voted");
  };
  const addProposal = () => {
    alert("Proposal added!");
  };
  const getVoter = () => {
    alert("see a voter");
  };
  const getOneProposal = () => {
    alert("see a voter");
  };

  return (
    <>
      <div className="addPrposal">
        <br />
        <h1 className="text-2xl">Add a proposal</h1>
        <input className="input" type="text" />

        <button display="block" className="btn" onClick={addProposal}>
          Click to add your proposal
        </button>
        <br />
      </div>
      <div className="getProp">
        <br />
        <h1 className="text-2xl">See a proposal</h1>
        <input className="input" type="text" />

        <button display="block" className="btn" onClick={getOneProposal}>
          Click to see a proposal
        </button>
        <br />
      </div>
      <div className="getVoter">
        <br />
        <h1 className="text-2xl">See a voter</h1>
        <input className="input" type="text" />

        <button display="block" className="btn" onClick={getVoter}>
          Click to see a voter
        </button>
        <br />
      </div>
      <div className="setVote">
        <br />
        <h1 className="text-2xl">Choose a proposal</h1>
        <br />
        <ul>
          <li>
            <p>Coffee</p>
            <button onClick="alert('Button 1')">Yes!</button>
          </li>
          <li>
            <p>Tea</p>
            <button onClick="alert('Button 2')">Yes!</button>
          </li>
          <li>
            <p>Milk</p>
            <button onClick="alert('Button 3')"></button>
          </li>
          <li>
            <p>Ice cream</p>
            <button onClick="alert('Button 4')">Yes!</button>
          </li>
          <li>
            <p>Fruits</p>
            <button onClick="alert('Button 4')">Yes!</button>
          </li>
          <li>
            <p>Cookies</p>
            <button onClick="alert('Button 4')">Yes!</button>
          </li>
        </ul>
        <br />
        <button display="block" className="btn" onClick={setVote}>
          Vote
        </button>
        <br />
      </div>
    </>
  );
}
