import Owner from "./components/Owner";
import Voter from "./components/Voter";
import "./style/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { EthProvider, useEth } from "./contexts/EthContext";
import { useEffect, useState, useCallback } from "react";

function App() {
  const { state } = useEth();
  const { contract, accounts } = state;

  const [isOwner, setIsOwner] = useState(false);
  const [isVoter, setIsVoter] = useState(false);
  const [address, setAddress] = useState(null);

  const getOwner = useCallback(async () => {
    const ownerAddress = await contract.methods.owner().call();
    if (ownerAddress === accounts[0]) {
      setIsOwner(true);
    }
  }, [contract, accounts, setIsOwner]);

  // const getVoter = useCallback(async () => {
  //   const voter = await contract.methods.getVoter(accounts[0]).call();
  // }, [contract]);

  useEffect(() => {
    // se relance à chaque fois que le contrat change de valeur

    if (contract) {
      getOwner();
      // getVoter();
    }
  }, [getOwner, contract]);

  //UseEffect

  // If Ok
  // setIsOwner(true)

  return (
    <div className="w-screen h-screen overflow-auto">
      {isOwner && <Owner />}
      <Voter />
    </div>
  );
}

export default App;
