import Owner from "./components/Owner";
import Voter from "./components/Voter";
import "../src/style/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { EthProvider, useEth } from "./contexts/EthContext";
import { useEffect, useState, useCallback } from "react";

function App() {
  // web3, useEffect()
  const { contract, account } = useEth();
  const [isOwner, setIsOwner] = useState(false);
  const [isVoter, setIsVoter] = useState(false);

  const getOwner = useCallback(async () => {
    const ownerAdress = await contract.method.Owner().call();

    if (ownerAdress === account[0]) {
      setIsOwner(true);
    }
  }, [contract, account, setIsOwner]);

  async function getVoter() {
    const voter = await contract.method.Voter().call();
  }

  useEffect(() => {
    getOwner();
    getVoter();
  }, [getOwner, getVoter]);

  //UseEffect

  // If Ok
  // setIsOwner(true)

  return (
    <div className="w-screen h-screen overflow-auto">
      {isOwner && <Owner />}
      <Voter />
    </div>
  );
}

export default App;
